#hamburger,#hamburger div{background-image:initial;background-attachment:initial;background-origin:initial;background-clip:initial;background-repeat:initial initial}h3,p,ul{margin:1em 0}body,html{width:100%;height:100%;margin:0;padding:0}header,nav,section{display:block}picture,video{display:inline-block}html{font-size:100%;font-family:sans-serif}h1{font-size:2em;margin:.67em 0}h2{font-size:1.5em;margin:.83em 0}h3{font-size:1.17em}ul{padding:0 0 0 40px}nav ul{list-style:none initial initial}img{border-style:initial;border-color:initial;border-width:0;border-width:0}svg:not(:root){overflow-x:hidden;overflow-y:hidden}.clearfix::after,.clearfix::before{content:' ';display:table}.clearfix::after{clear:both}*{max-width:100%;box-sizing:border-box}body{background-color:red;font-size:14px}#hamburger{position:fixed;right:.5em;top:1em;height:2em;width:2.25em;background-color:initial;z-index:1000;background-position:0 0;padding:.25em}#hamburger div{background-color:#fff;height:3px;-webkit-transition-property:all;-webkit-transition-duration:175ms;-webkit-transition-timing-function:ease-in-out;-webkit-transition-delay:initial;background-position:initial initial}#hamburger div:first-of-type{margin-top:1.5px}#hamburger div+div{margin-top:3px}@media screen and (min-width:768px){#hamburger{display:none}}@media screen and (min-width:993px){body{font-size:16px}}@media print{img{page-break-inside:avoid;max-width:100%!important}*{background-image:initial!important;background-attachment:initial!important;background-origin:initial!important;background-clip:initial!important;background-color:initial!important;color:#000!important;box-shadow:none!important;text-shadow:none!important;background-position:0 0!important;background-repeat:initial initial!important}a{text-decoration:underline}a[href]::after{content:' (',attr(href),')'}a[href^="#"]::after{content:''}h2,h3,p{orphans:3;widows:3}h2,h3{page-break-after:avoid}}